return items.map(item => {
  const word = String(item.json.output ?? '');
  const binaryData = item.binary?.data;
  if (!binaryData) throw new Error("No binary data found in item");

  let safeFileName = word
    .normalize('NFKC')
    .replace(/[<>:"/\\|?*\x00-\x1F]/g, '') // remove invalid filename & control chars
    .replace(/^[\s\u00A0\uFEFF\u200B\u200C\u200D\u2060]+|[\s\u00A0\uFEFF\u200B\u200C\u200D\u2060]+$/g, '') // trim many unicode spaces
    .replace(/\s+/g, ' ') // collapse multiple spaces into one
    .trim();

  if (!safeFileName) safeFileName = `untitled-${Date.now()}`;

  item.binary.data.fileName = `${safeFileName}.md`;
  return item;
});

